// components/AdminPages/UserList.js

import React, { useState, useEffect } from 'react';
import api from '../../api';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [roleFilter, setRoleFilter] = useState('all');

  useEffect(() => {
    // Fetch users from the backend
    const fetchUsers = async () => {
      try {
        const response = await api.get('/users');
        setUsers(response.data);
        setFilteredUsers(response.data); // Initially, display all users
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };
    fetchUsers();
  }, []);

  useEffect(() => {
    // Filter users by role
    if (roleFilter === 'all') {
      setFilteredUsers(users);
    } else {
      setFilteredUsers(users.filter(user => user.role === roleFilter));
    }
  }, [roleFilter, users]);

  const makeOrganizer = async (userId) => {
    try {
      await api.patch('/users/update-role', { userId, newRole: 'organizer' });
      setUsers(users.map(user => user._id === userId ? { ...user, role: 'organizer' } : user));
    } catch (error) {
      console.error('Error updating role to organizer:', error);
    }
  };

  const makeStudent = async (userId) => {
    try {
      await api.patch('/users/update-role', { userId, newRole: 'student' });
      setUsers(users.map(user => user._id === userId ? { ...user, role: 'student' } : user));
    } catch (error) {
      console.error('Error updating role to student:', error);
    }
  };

  const deleteUser = async (userId) => {
    try {
      await api.delete(`/users/${userId}`);
      setUsers(users.filter(user => user._id !== userId));
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };


  return (
    <div className='mt-8 p-4 mx-20 bg-white flex-col rounded-md'>
      <h1 className="text-2xl font-bold inline ">Users</h1>

      {/* Role filter dropdown */}
      <div className='flex '>
            <label htmlFor="roleFilter" className=" text-sm font-bold text-gray-700 mt-4 mr-4">Filter by Role</label>
            <select
                id="roleFilter"
                value={roleFilter}
                onChange={(e) => setRoleFilter(e.target.value)}
                className="mt-2 p-2 border border-gray-300 rounded"
            >
                <option value="all">All</option>
                <option value="student">Student</option>
                <option value="organizer">Organizer</option>
                <option value="admin">Admin</option>
            </select>
      </div>


      {/* Display users */}
      <div className="mt-4">
        {filteredUsers.map(user => (
          <div key={user._id} className="p-4 border border-gray-300 rounded mb-2 shadow">
            <div className='flex justify-between'>
                <p><strong>Name:</strong> {user.full_name}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Role:</strong> {user.role}</p>
            </div>
            {user.role!='admin'?<hr className='mt-2'></hr>:""}
            <div className='mt-2 flex justify-around'>
            {user.role !== 'admin' ? (
                    user.role === "student" ? (
                      <button
                        className="justify-center w-36 py-1 px-1 font-semibold bg-orange-500 hover:bg-orange-600"
                        onClick={() => makeOrganizer(user._id)}
                      >
                        Make Organizer
                      </button>
                    ) : (
                      <button
                        className="justify-center w-40 py-1 px-1 font-semibold bg-indigo-500 hover:bg-indigo-600"
                        onClick={() => makeStudent(user._id)}
                      >
                        Remove Organizer
                      </button>
                    )
                  ) : (
                    ""
                  )}

            {user.role!='admin'?<button className="justify-center w-36 py-1 px-1 font-semibold bg-red-500 hover:bg-red-600" onClick={() => deleteUser(user._id)}>Delete User</button>:""}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserList;
