import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import api from '../api'; // Your Axios instance or API configuration
import { useAuth } from '../context/AuthContext'; // Assuming you have an AuthContext for user data

const EventRegistrationPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { event } = location.state || {}; // Get event details passed from the EventList
  const { user } = useAuth(); // Get user info from AuthContext
  const [teamName, setTeamName] = useState('');
  const [teamMembers, setTeamMembers] = useState(Array(event?.teamSize).fill(''));

  useEffect(() => {
    // Check if user is logged in
    if (!user) {
      alert('You must be logged in to register.'); // Check if user is logged in
      navigate('/login');
    }
  }, [user, navigate]);

  const handleIndividualRegister = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('token'); // Retrieve token from local storage
      console.log("email=",user.email);
      await api.post(
        `/events/${event._id}/register/individual`,
        { email: user.email }, // Use user's email from AuthContext
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert('Registration successful!');
      navigate('/events'); // Redirect back to events list
    } catch (error) {
        console.log(error);
      alert('Registration failed: ' + error.response.data.message);
    }
  };

  const handleTeamRegister = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token'); // Retrieve token from local storage
      await api.post(
        `/events/${event._id}/register/team`, 
        { teamName, members: teamMembers },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert('Team registration successful!');
      navigate('/events'); // Redirect back to events list
    } catch (error) {
      alert('Registration failed: ' + error.message);
    }
  };

  const handleTeamMemberChange = (index, value) => {
    const updatedMembers = [...teamMembers];
    updatedMembers[index] = value;
    setTeamMembers(updatedMembers);
  };

  return (
    <div className="container mx-auto px-4 mt-8">
      <h1 className="text-2xl font-bold mb-4">
        {event?.eventType === 'team' ? 'Team Registration' : 'Individual Registration'} for {event?.name}
      </h1>

      {event?.eventType === 'individual' ? (
        <form onSubmit={handleIndividualRegister}>
          <p><strong>Email:</strong> {user?.email}</p> {/* Display the logged-in user's email */}
          <button
            type="submit"
            className="mt-4 w-full py-2 text-white font-bold rounded bg-indigo-600 hover:bg-indigo-700 focus:outline-none"
          >
            Register
          </button>
        </form>
      ) : (
        <form onSubmit={handleTeamRegister}>
          <label htmlFor="teamName" className="block text-sm font-medium text-gray-700">Team Name</label>
          <input
            type="text"
            id="teamName"
            value={teamName}
            onChange={(e) => setTeamName(e.target.value)}
            required
            className="mt-1 p-2 border border-gray-300 rounded w-full"
          />
          <label className="block text-sm font-medium text-gray-700 mt-2">Team Members</label>
          {teamMembers.map((member, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Member ${index + 1} Name`}
              value={member}
              onChange={(e) => handleTeamMemberChange(index, e.target.value)}
              className="mt-1 p-2 border border-gray-300 rounded w-full mb-1"
              required
            />
          ))}
          <button
            type="submit"
            className="mt-4 w-full py-2 text-white font-bold rounded bg-indigo-600 hover:bg-indigo-700 focus:outline-none"
          >
            Register Team
          </button>
        </form>
      )}
    </div>
  );
};

export default EventRegistrationPage;
