import React from 'react';
import Footer from '../components/Footer';

const Sponsors = () => {
  // Function to import all images from a folder
  const importImages = (folderPath) => {
    const images = [];
    const context = require.context(`../assets/${folderPath}`, false, /\.(png|jpe?g|svg)$/);
    context.keys().forEach((key) => images.push(context(key)));
    return images;
  };

  // Import images from folders
  const currentSponsorImages = importImages('currentSponsors');
  const pastSponsorImages = importImages('pastSponsors');

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Header Section */}
      <header className="bg-indigo-600 text-white py-10">
        <h1 className="text-4xl font-bold text-center">Our Sponsors</h1>
        <p className="text-center mt-2 text-lg">
          We are proudly supported by industry-leading companies and organizations.
        </p>
      </header>

      {/* Current Sponsors Section */}
      <section className="flex-grow container mx-auto py-10 px-4">
        <h2 className="text-2xl font-semibold text-center mb-6">Current Sponsors</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-8">
          {currentSponsorImages.map((src, index) => (
            <img
              key={index}
              src={src}
              alt={`Current Sponsor ${index + 1}`}
              className="w-full h-auto rounded-lg shadow-lg transform hover:scale-105 transition-transform duration-200"
            />
          ))}
        </div>
      </section>

      {/* Past Sponsors Section */}
      <section className="container mx-auto py-10 px-4 bg-white">
        <h2 className="text-2xl font-semibold text-center mb-6">Our Past Sponsors</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {pastSponsorImages.map((src, index) => (
            <div
              key={index}
              className="bg-gray-100 rounded-lg shadow-lg p-4 flex flex-col items-center text-center"
            >
              <img
                src={src}
                alt={`Past Sponsor ${index + 1}`}
                className="w-24 h-24 mb-4 rounded-full shadow-md"
              />
              <h3 className="text-lg font-semibold">Sponsor {index + 1}</h3>
              <p className="text-gray-700 text-sm mt-2">Thank you for supporting us!</p>
            </div>
          ))}
        </div>
      </section>

      {/* Become a Sponsor Section */}
      <section className="bg-indigo-100 py-10 text-center">
        <h2 className="text-2xl font-bold mb-4">Interested in Sponsoring Us?</h2>
        <p className="max-w-xl mx-auto text-gray-700 mb-6">
          Partner with us and gain visibility in our community. We offer a range of sponsorship 
          opportunities to suit your brand and goals.
        </p>
        <button className="bg-indigo-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-indigo-700 transition">
          Contact Us for Sponsorship
        </button>
      </section>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Sponsors;
