import React, { useEffect, useState } from 'react';
import api from '../api'; // Your Axios instance or API configuration

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [registrationData, setRegistrationData] = useState({
    eventId: '',
    participantName: '',
    teamName: '', // For team registration
    teamMembers: [], // Array to hold team members
  });

  useEffect(() => {
    // Fetch events from the backend
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events'); // Adjust the endpoint if necessary
        setEvents(response.data); // Assuming the data from backend is in the right format
      } catch (err) {
        setError('Error fetching events');
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, []);

  const handleRegistrationChange = (e) => {
    const { name, value } = e.target;
    setRegistrationData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleTeamRegistrationChange = (e, index) => {
    const newTeamMembers = [...registrationData.teamMembers];
    newTeamMembers[index] = e.target.value;
    setRegistrationData((prevData) => ({
      ...prevData,
      teamMembers: newTeamMembers,
    }));
  };

  const handleRegister = async (event) => {
    event.preventDefault();
    const { eventId, participantName, teamName, teamMembers } = registrationData;

    try {
      if (event.eventType === 'individual') {
        await api.post(`/events/${eventId}/register/individual`, { name: participantName });
      } else {
        await api.post(`/events/${eventId}/register/team`, { teamName, members: teamMembers });
      }
      // Clear the form after submission
      setRegistrationData({ eventId: '', participantName: '', teamName: '', teamMembers: [] });
      alert('Registration successful!');
    } catch (error) {
      alert('Registration failed: ' + error.message);
    }
  };

  if (loading) return <p>Loading events...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="container mx-auto px-4">
      <h1 className="text-3xl font-bold text-center mt-8 mb-4">All Events</h1>

      {events.length === 0 ? (
        <p className="text-center text-gray-500">No events available</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {events.map((event) => (
            <div key={event._id} className="p-4 bg-white shadow rounded-lg">
              <h2 className="text-xl font-semibold mb-2">{event.name}</h2>
              <p className="text-gray-700 mb-1"><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
              <p className="text-gray-700 mb-1"><strong>Location:</strong> {event.location}</p>
              <p className="text-gray-700 mb-1"><strong>Sport Type:</strong> {event.sportType}</p>
              <p className="text-gray-700 mb-1"><strong>Max Participants:</strong> {event.maxParticipants}</p>
              <p className="text-gray-700 mb-1"><strong>Event Type:</strong> {event.eventType}</p>
              <p className="text-gray-700 mb-1">
                <strong>Organizer:</strong> {event.organizer?.full_name || "Unknown"}
              </p>
              <p className="text-gray-700 mb-1"><strong>Organizer Email:</strong> {event.organizer?.email || "N/A"}</p>

              {/* Registration Form */}
              <form onSubmit={handleRegister}>
                <input
                  type="hidden"
                  name="eventId"
                  value={event._id}
                  onChange={handleRegistrationChange}
                />
                {event.eventType === 'individual' ? (
                  <>
                    <label htmlFor="participantName" className="block text-sm font-medium text-gray-700">Your Name</label>
                    <input
                      type="text"
                      id="participantName"
                      name="participantName"
                      value={registrationData.participantName}
                      onChange={handleRegistrationChange}
                      required
                      className="mt-1 p-2 border border-gray-300 rounded w-full"
                    />
                  </>
                ) : (
                  <>
                    <label htmlFor="teamName" className="block text-sm font-medium text-gray-700">Team Name</label>
                    <input
                      type="text"
                      id="teamName"
                      name="teamName"
                      value={registrationData.teamName}
                      onChange={handleRegistrationChange}
                      required
                      className="mt-1 p-2 border border-gray-300 rounded w-full"
                    />
                    <label className="block text-sm font-medium text-gray-700 mt-2">Team Members</label>
                    {Array.from({ length: event.maxParticipants }).map((_, index) => (
                      <input
                        key={index}
                        type="text"
                        placeholder={`Member ${index + 1} Name`}
                        value={registrationData.teamMembers[index] || ''}
                        onChange={(e) => handleTeamRegistrationChange(e, index)}
                        className="mt-1 p-2 border border-gray-300 rounded w-full mb-1"
                        required
                      />
                    ))}
                  </>
                )}
                <button
                  type="submit"
                  className="mt-4 w-full py-2 text-white font-bold rounded bg-indigo-600 hover:bg-indigo-700 focus:outline-none"
                >
                  Register
                </button>
              </form>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default EventList;
