import React, { useState, useEffect } from 'react';
import api from '../../api';

const EventParticipants = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState('');
  const [participants, setParticipants] = useState([]);
  const [isTeamEvent, setIsTeamEvent] = useState(false);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get('/events');
        setEvents(response.data);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };
    fetchEvents();
  }, []);

  const handleEventSelect = async (eventId) => {
    try {
      setSelectedEvent(eventId);
      const token=localStorage.getItem('token');
      const response = await api.get(`/events/${eventId}/participants`,{
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = response.data;

      if (data.teams) {
        setParticipants(data.teams);
        setIsTeamEvent(true);
      } else {
        setParticipants(data.participants);
        setIsTeamEvent(false);
      }
    } catch (error) {
      console.error('Error fetching participants:', error);
    }
  };

  return (
    <div className="mt-8 p-4 bg-white flex-col rounded-md">
      <h1 className="text-2xl font-bold mb-4">Event Participants</h1>

      {/* Event selection dropdown */}
      <label htmlFor="eventSelect" className="text-sm font-bold text-gray-700 mr-4">
        Select Event:
      </label>
      <select
        id="eventSelect"
        onChange={(e) => handleEventSelect(e.target.value)}
        className="p-2 border border-gray-300 rounded"
      >
        <option value="">Select an event</option>
        {events.map((event) => (
          <option key={event._id} value={event._id}>
            {event.name}
          </option>
        ))}
      </select>

      {/* Display participants or teams */}
      <div className="mt-6">
        {isTeamEvent ? (
          participants.map((team, index) => (
            <div key={index} className="p-4 border border-gray-300 rounded mb-2">
              <p className="font-semibold">Team: {team.teamName}</p>
              <ul>
                {team.members.map((member, i) => (
                  <li key={i}>Member Email: {member.email}</li>
                ))}
              </ul>
            </div>
          ))
        ) : (
          participants.map((participant, index) => (
            <div key={index} className="p-4 border border-gray-300 rounded mb-2">
              <p>Participant Email: {participant.email}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default EventParticipants;
