// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Homepage from './pages/HomePage';
import Dashboard from './pages/Dashboard'; // Make sure to create this component
import Login from './pages/LoginPage';
import Register from './pages/RegisterPage';
import Sidebar from './components/Sidebar';
import './App.css'; // Make sure this line is at the top
import { LifeBuoy,Receipt,Boxes,Package,UserCircle,BarChart3,LayoutDashboard,Settings } from 'lucide-react';
import { SidebarItem } from './components/Sidebar';
function App() {
   return (
      <Router>
         <div className="app">
            <Sidebar> 
              <SidebarItem icon={<LayoutDashboard size={20}/>} text="Dashboard" />
              <SidebarItem icon={<BarChart3 size={20}/>} text="Statistics" active/>
              <SidebarItem icon={<Boxes size={20}/>} text="Inventory" />
              <SidebarItem icon={<Package size={20}/>} text="Orders" alert/>
              <hr className='my-3'/>
              <SidebarItem icon={<LayoutDashboard size={20}/>} text="Dashboard" />
            </Sidebar>

         </div>
      </Router>
   );
}

export default App;
