// src/pages/HomePage.js
import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from '../api';
import { AuthContext } from '../context/AuthContext'

const HomePage = () => {
   const { user } = useContext(AuthContext);
   const [upcomingEvents, setUpcomingEvents] = useState([]);

   useEffect(() => {
      const fetchUpcomingEvents = async () => {
         try {
            const response = await axios.get('/events/upcoming');
            setUpcomingEvents(response.data);
         } catch (error) {
            console.error('Failed to fetch events', error);
         }
      };
      fetchUpcomingEvents();
   }, []);

   return (
      <div className="homepage">
         <header className="banner">
            <h1>Welcome to College Sports Event Management</h1>
            <p>Join exciting events, track live scores, and compete with your peers!</p>
         </header>

         <section className="upcoming-events">
            <h2>Upcoming Events</h2>
            {upcomingEvents.length > 0 ? (
               <div className="event-list">
                  {upcomingEvents.map((event) => (
                     <div key={event._id} className="event-item">
                        <h3>{event.event_name}</h3>
                        <p>Sport: {event.sport}</p>
                        <p>Date: {new Date(event.event_date).toLocaleDateString()}</p>
                        <Link to={`/events/${event._id}`} className="details-link">View Details</Link>
                     </div>
                  ))}
               </div>
            ) : (
               <p>No upcoming events at the moment.</p>
            )}
         </section>


         <footer className="auth-links">
            {user ? (
               <p>Welcome, {user.full_name}! Check your <Link to="/dashboard">Dashboard</Link> for more options.</p>
            ) : (
               <div>
                  <Link to="/login" className="login-link">Login</Link> or{' '}
                  <Link to="/register" className="register-link">Register</Link> to participate!
               </div>
            )}
         </footer>
      </div>
   );
};

export default HomePage;
