import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.min.css';
import { Pagination, Autoplay } from 'swiper';

// Function to import images
const importAll = (r) => {
  let images = [];
  r.keys().forEach((item) => {
    images.push(r(item));
  });
  return images;
};

// Import all images from the specified folder
const images = importAll(require.context('../assets/gallery', false, /\.(png|jpe?g|svg)$/));

const ImageGallery = () => {
  return (
    <Swiper
      spaceBetween={30}
      pagination={{ clickable: true }}
      autoplay={{ delay: 5000, disableOnInteraction: false }}
      loop={true}
      modules={[Pagination, Autoplay]} // Add modules here
    >
      {images.map((image, index) => (
        <SwiperSlide key={index}>
          <img src={image.default} alt={`Slide ${index + 1}`} className="w-full h-auto" />
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default ImageGallery;
