import React, { useState } from 'react';

// Import all images from the "assets/gallery" folder dynamically
const importAll = (r) => r.keys().map(r);
const images = importAll(require.context('../assets/gallery', false, /\.(png|jpe?g|svg)$/));

const ImageGallery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Functions to handle the navigation
  const prevImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <section className="py-16 bg-gray-200">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold mb-10">Image Gallery</h2>

        {/* Gallery Slider */}
        <div className="relative flex items-center justify-center">
          <button
            onClick={prevImage}
            className="absolute left-0 bg-gray-800 text-white p-2 rounded-full focus:outline-none"
          >
            &lt;
          </button>

          <img
            src={images[currentIndex]}
            alt={`Gallery Image ${currentIndex + 1}`}
            className="w-full max-w-lg h-auto rounded-lg shadow-lg"
          />

          <button
            onClick={nextImage}
            className="absolute right-0 bg-gray-800 text-white p-2 rounded-full focus:outline-none"
          >
            &gt;
          </button>
        </div>
      </div>
    </section>
  );
};

export default ImageGallery;
