import React, { useState, useEffect } from 'react';
import api from '../api';
import vs from '../assets/vs.png';

const LiveMatches = () => {
  const [liveMatches, setLiveMatches] = useState([]);

  useEffect(() => {
    const fetchLiveMatches = async () => {
      try {
        const response = await api.get('/matches');
        const currentTime = new Date();

        const live = response.data.filter((match) => {
          const matchStartTime = new Date(`${match.date.substring(0, 10)}T${match.time}:00`);
          const matchEndTime = new Date(matchStartTime.getTime() + match.duration * 60000);
          return currentTime >= matchStartTime && currentTime <= matchEndTime;
        });

        setLiveMatches(live);
      } catch (error) {
        console.error('Error fetching live matches:', error);
      }
    };

    fetchLiveMatches();
  }, []);

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 my-4">
      <h2 className="text-2xl font-bold mb-4 flex items-center">
        {liveMatches.length > 0 && (
          <span className='flex m-2'>
                    <span class="relative flex h-3 w-3">
                        <span class="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
                        <span class="relative inline-flex rounded-full h-3 w-3 bg-red-500"></span>
                    </span>
          </span>
        )}
        Live Matches
      </h2>

      <div className=''>
            {liveMatches.length > 0 ? (
                liveMatches.map((match, index) => (
                <div key={index} className="p-4 mb-4 border rounded-lg">
                    <p className='text-black font-extrabold'>{match.event.name}</p>
                    <p className='flex'>
                       <span className='bg-red-50 shadow-md border border-red-500 p-1 rounded-lg my-1 font-bold'>{match.team1 || match.player1} </span> 
                        <img src={vs} className='w-8 h-8 p-2' /> 
                        <span className='bg-blue-50 shadow-md border border-blue-500 p-1 rounded-lg my-1 font-bold'> {match.team2 || match.player2}</span> 
                    </p>
                    <p><strong>Venue:</strong> {match.venue}</p>
                    <p><strong>Start Time:</strong> {match.time}</p>
                </div>
                ))
            ) : (
                <p>No live matches currently.</p>
            )}
      </div>

    </div>
  );
};

export default LiveMatches;
