import React, { useState, useEffect } from 'react';
import api from '../api';

const UpcomingMatches = () => {
  const [upcomingMatches, setUpcomingMatches] = useState([]);

  useEffect(() => {
    const fetchUpcomingMatches = async () => {
      try {
        const response = await api.get('/matches');
        const currentTime = new Date();
        const twoHoursLater = new Date(currentTime.getTime() + 2 * 60 * 60 * 1000);

        const upcoming = response.data.filter((match) => {
          // Combine match.date and match.time into a single Date object
          const matchDateTime = new Date(`${match.date.substring(0, 10)}T${match.time}:00`);

          // Log for debugging purposes
        //   console.log("Match Start Time:", matchDateTime);
        //   console.log("Current Time:", currentTime);
        //   console.log("Two Hours Later:", twoHoursLater);

          // Check if match start time is within the next 2 hours
          return matchDateTime > currentTime && matchDateTime <= twoHoursLater;
        });
        setUpcomingMatches(upcoming);
      } catch (error) {
        console.error('Error fetching upcoming matches:', error);
      }
    };

    fetchUpcomingMatches();
  }, []);

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 my-4">
      <h2 className="text-2xl font-bold mb-4">Upcoming Matches (Next 2 Hours)</h2>
      {upcomingMatches.length > 0 ? (
        upcomingMatches.map((match, index) => (
          <div key={index} className="p-4 mb-4 border rounded-lg">
            <p><strong>Event:</strong> {match.event.name}</p>
            <p><strong>Teams/Players:</strong> {match.team1 || match.player1} vs {match.team2 || match.player2}</p>
            <p><strong>Venue:</strong> {match.venue}</p>
            <p><strong>Start Time:</strong> {match.time}</p>
          </div>
        ))
      ) : (
        <p>No upcoming matches within the next 2 hours.</p>
      )}
    </div>
  );
};

export default UpcomingMatches;
