import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Carousel CSS

// Function to import all images in a folder
const importAllImages = (requireContext) => {
  return requireContext.keys().map(requireContext);
};

// Import images from the "gallery" folder
const images = importAllImages(require.context('../assets/bannerImages', false, /\.(png|jpe?g|svg)$/));

const Banner = () => {
  return (
    <section className="">
      <Carousel autoPlay infiniteLoop interval={3000} showThumbs={false} showStatus={false}>
        {images.map((image, index) => (
          <div key={index}>
            <img
              src={image.default} // For some setups, this may need to be just `image` based on your configuration.
              alt={`Event ${index + 1}`}
              className="w-screen h-[60vh] object-cover"
            />
          </div>
        ))}
      </Carousel>

      <div className="container mx-auto text-center mt-10">
        <h1 className="text-5xl font-bold mb-4">Welcome to Asmita</h1>
        <p className="text-lg mb-8">IIITA Sports Fest</p>
      </div>
    </section>
  );
};

export default Banner;
