import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Carousel CSS

// Function to import images
const importAll = (r) => {
    let images = [];
    r.keys().forEach((item) => {
        images.push(r(item));
    });
    return images;
};

// Import all images from the specified folder
const images = importAll(
    require.context("../assets/bannerImages", false, /\.(png|jpe?g|svg)$/)
);

const Banner = () => {
    return (
        <section className="">
            <Carousel 
                autoPlay 
                infiniteLoop 
                interval={3000} // Adjust the interval as needed
                showThumbs={false} 
                showStatus={false} 
                stopOnHover={false} // Ensure this is set to false
                dynamicHeight // Optional: If you want the height to adapt to content
            >
                {images.map((image, index) => (
                    <div key={index}>
                        <img
                            src={image}
                            alt={`Event ${index + 1}`}
                            className="w-screen h-[70vh] object-cover" // Responsive styling
                        />
                    </div>
                ))}
            </Carousel>

            <div className="container mx-auto text-center mt-10">
                <h1 className="libre-baskerville-bold text-5xl font-bold mb-4">Welcome to ASMITA 2024</h1>
                <p className="libre-baskerville-bold text-lg mb-8">IIITA Sports Fest</p>
            </div>
        </section>
    );
};

export default Banner;
