import React, { useState, useEffect } from 'react';
import api from '../api';

const LiveMatches = () => {
  const [liveMatches, setLiveMatches] = useState([]);

  useEffect(() => {
    const fetchLiveMatches = async () => {
      try {
        const response = await api.get('/matches');
        const currentTime = new Date();

        const live = response.data.filter((match) => {
          const matchStartTime = new Date(`${match.date.substring(0, 10)}T${match.time}:00`);
          const matchEndTime = new Date(matchStartTime.getTime() + match.duration * 60000);
          return currentTime >= matchStartTime && currentTime <= matchEndTime;
        });

        setLiveMatches(live);
      } catch (error) {
        console.error('Error fetching live matches:', error);
      }
    };

    fetchLiveMatches();
  }, []);

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 my-4">
      <h2 className="text-2xl font-bold mb-4 flex items-center">
        Live Matches
        {liveMatches.length > 0 && (
          <span className="ml-2 h-3 w-3 bg-red-500 rounded-full animate-blink"></span>
        )}
      </h2>
      {liveMatches.length > 0 ? (
        liveMatches.map((match, index) => (
          <div key={index} className="p-4 mb-4 border rounded-lg">
            <p><strong>Event:</strong> {match.event.name}</p>
            <p><strong>Teams/Players:</strong> {match.team1 || match.player1} vs {match.team2 || match.player2}</p>
            <p><strong>Venue:</strong> {match.venue}</p>
            <p><strong>Start Time:</strong> {match.time}</p>
          </div>
        ))
      ) : (
        <p>No live matches currently.</p>
      )}
    </div>
  );
};

export default LiveMatches;
