import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css'; // Import Swiper core CSS
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import { Pagination, Autoplay } from 'swiper/modules'; // Import modules

// Function to import images
const importAll = (r) => {
  let images = [];
  r.keys().forEach((item) => {
    images.push(r(item).default); // Update this if images aren't loading correctly
  });
  return images;
};

// Import all images from the specified folder
const images = importAll(require.context('../assets/gallery', false, /\.(png|jpe?g|svg)$/));

const ImageGallery = () => {
  return (
    <Swiper
      spaceBetween={30}
      pagination={{ clickable: true }}
      autoplay={{ delay: 5000, disableOnInteraction: false }}
      loop={true}
      modules={[Pagination, Autoplay]}
      className="w-full h-64" // Temporary height for Swiper container
    >
      {images.map((image, index) => (
        <SwiperSlide key={index} className="w-full h-64 flex justify-center items-center border border-gray-200"> {/* Border to confirm visibility */}
          <img src={image} alt={`Slide ${index + 1}`} className="object-contain w-full h-full" /> {/* Object-contain for better scaling */}
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default ImageGallery;
