import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext'; // Use AuthContext for the organizer ID if required
import { useNavigate } from 'react-router-dom';
import axios from '../../api'; // Adjust if your axios instance is in another path

const CreateEvent = () => {
   const { user } = useAuth(); // Assuming the user context provides organizer's info
   const navigate = useNavigate();

   const [formData, setFormData] = useState({
      name: '',
      description: '',
      date: '',
      location: '',
      sportType: '',
      maxParticipants: 0,
   });
   const [error, setError] = useState(null);
   const [loading, setLoading] = useState(false);

   const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData((prevData) => ({
         ...prevData,
         [name]: value,
      }));
   };

   const handleSubmit = async (e) => {
      e.preventDefault();
      setError(null);
      setLoading(true);

      try {
         await axios.post('/events', {
            ...formData,
            organizer: user?._id, // Include the organizer ID from context
         });
         navigate('/events'); // Redirect to events list or another page upon success
      } catch (err) {
         setError('Failed to create event. Please try again.');
      } finally {
         setLoading(false);
      }
   };

   return (
      <div className="flex justify-center items-center  bg-gray-100">
         <div className="w-full max-w-lg bg-white p-8 rounded shadow-md">
            <h1 className="text-2xl font-bold mb-4 text-center">Create Event</h1>
            <form onSubmit={handleSubmit}>
               <div className="mb-4">
                  <label htmlFor="name" className="block text-sm font-medium text-gray-700">Event Name</label>
                  <input
                     type="text"
                     id="name"
                     name="name"
                     value={formData.name}
                     onChange={handleChange}
                     required
                     className="mt-1 p-2 border border-gray-300 rounded w-full"
                  />
               </div>
               <div className="mb-4">
                  <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
                  <textarea
                     id="description"
                     name="description"
                     value={formData.description}
                     onChange={handleChange}
                     className="mt-1 p-2 border border-gray-300 rounded w-full"
                  />
               </div>
                <div className='flex justify-end'>
                    <div className="mb-4">
                        <label htmlFor="date" className="justify-normal text-sm font-medium text-gray-700">Date</label>
                        <input
                            type="date"
                            id="date"
                            name="date"
                            value={formData.date}
                            onChange={handleChange}
                            required
                            className="mt-1 p-2 border border-gray-300 rounded w-full"
                        />
                    </div>
                    <div className="mb-4">
                        <label htmlFor="location" className="text-sm font-medium text-gray-700">Location</label>
                        <input
                            type="text"
                            id="location"
                            name="location"
                            value={formData.location}
                            onChange={handleChange}
                            required
                            className="mt-1 p-2 border border-gray-300 rounded w-full"
                        />
                    </div>
                </div>
               <div className="mb-4">
                  <label htmlFor="sportType" className="block text-sm font-medium text-gray-700">Sport Type</label>
                  <input
                     type="text"
                     id="sportType"
                     name="sportType"
                     value={formData.sportType}
                     onChange={handleChange}
                     required
                     className="mt-1 p-2 border border-gray-300 rounded w-full"
                  />
               </div>
               <div className="mb-4">
                  <label htmlFor="maxParticipants" className="block text-sm font-medium text-gray-700">Max Participants</label>
                  <input
                     type="number"
                     id="maxParticipants"
                     name="maxParticipants"
                     value={formData.maxParticipants}
                     onChange={handleChange}
                     required
                     className="mt-1 p-2 border border-gray-300 rounded w-full"
                  />
               </div>
               {error && <p className="text-red-500">{error}</p>}
               <button
                  type="submit"
                  className={`mt-6 w-full py-3 text-white font-bold rounded bg-indigo-600 hover:bg-indigo-700 focus:outline-none ${
                     loading ? 'opacity-50 cursor-not-allowed' : ''
                  }`}
                  disabled={loading}
               >
                  {loading ? 'Creating Event...' : 'Create Event'}
               </button>
            </form>
         </div>
      </div>
   );
};

export default CreateEvent;
