import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext'; // Import Auth context
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
   const { login } = useAuth();
   const [email, setEmail] = useState('');
   const [password, setPassword] = useState('');
   const [error, setError] = useState(null); // State to hold error messages
   const navigate = useNavigate();

   const handleLogin = async (e) => {
      e.preventDefault();
      setError(null); // Reset error state on new login attempt

      try {
         // Attempt to login with the provided email and password
         await login({ email, password });
         navigate('/dashboard'); // Redirect to dashboard on successful login
      } catch (err) {
         // Set error message based on the error from the server
         setError(err.response?.data?.error || 'Login failed, please try again.');
      }
   };

   function Button({ value }) {
      return (
         <button 
            type="submit" // Ensure this is a submit button
            className="mt-6 transition transition-all block py-3 px-4 w-full text-white font-bold rounded cursor-pointer bg-gradient-to-r from-indigo-600 to-purple-400 hover:from-indigo-700 hover:to-purple-500 focus:bg-indigo-900 transform hover:-translate-y-1 hover:shadow-lg">
            {value}
         </button>
      );
   }

   function Input({ type, id, name, label, placeholder, autofocus, value, onChange }) {
      return (
         <label className="text-gray-500 block mt-3">{label}
            <input
               autoFocus={autofocus}
               type={type} 
               id={id} 
               name={name} 
               placeholder={placeholder}
               value={value} // Set the value prop
               onChange={onChange} // Pass down the onChange function
               className="rounded px-4 py-3 w-full mt-1 bg-white text-gray-900 border border-gray-200 focus:border-indigo-400 focus:outline-none focus:ring focus:ring-indigo-100"/>
         </label>
      );
   }

   return (
      <div className="bg-gray-200 flex justify-center items-center h-screen">
         <div className="border-t-8 rounded-sm border-indigo-600 bg-white p-12 shadow-2xl w-96">
            <h1 className="font-bold text-center block text-2xl">Log In</h1>
            <form onSubmit={handleLogin}>
               <Input 
                  type="email" 
                  id="email" 
                  name="email" 
                  label="Email Address" 
                  placeholder="example@iiita.ac.in" 
                  value={email} // Pass the current state as the value
                  onChange={(e) => setEmail(e.target.value)} // Directly update the state
               />
               <Input 
                  type="password" 
                  id="password" 
                  name="password" 
                  label="Password" 
                  placeholder="••••••••••" 
                  value={password} // Pass the current state as the value
                  onChange={(e) => setPassword(e.target.value)} // Directly update the state
               />
               {error && <p className="text-red-500">{error}</p>} {/* Display error message */}
               <Button value="Login" />
            </form>
         </div>
      </div>
   );
};

export default LoginPage;
