// components/AdminPages/UserList.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [roleFilter, setRoleFilter] = useState('all');

  useEffect(() => {
    // Fetch users from the backend
    const fetchUsers = async () => {
      try {
        const response = await axios.get('/api/users', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } // Use your token here
        });
        setUsers(response.data);
        setFilteredUsers(response.data); // Initially, display all users
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, []);

  useEffect(() => {
    // Filter users by role
    if (roleFilter === 'all') {
      setFilteredUsers(users);
    } else {
      setFilteredUsers(users.filter(user => user.role === roleFilter));
    }
  }, [roleFilter, users]);

  return (
    <div>
      <h2 className="text-2xl font-bold">User List</h2>

      {/* Role filter dropdown */}
      <label htmlFor="roleFilter" className="block text-sm font-medium text-gray-700 mt-4">Filter by Role</label>
      <select
        id="roleFilter"
        value={roleFilter}
        onChange={(e) => setRoleFilter(e.target.value)}
        className="mt-2 p-2 border border-gray-300 rounded"
      >
        <option value="all">All</option>
        <option value="student">Student</option>
        <option value="organizer">Organizer</option>
        <option value="admin">Admin</option>
      </select>

      {/* Display users */}
      <div className="mt-4">
        {filteredUsers.map(user => (
          <div key={user._id} className="p-4 border border-gray-300 rounded mb-2 shadow">
            <p><strong>Name:</strong> {user.full_name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserList;
