import React, { useState } from "react";

// Function to import images
const importAll = (r) => {
  let images = [];
  r.keys().forEach((item) => {
    images.push(r(item));
  });
  return images;
};

// Import all images from the specified folder
const images = importAll(
  require.context("../assets/gallery", false, /\.(png|jpe?g|svg)$/)
);

const ImageGallery = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + images.length) % images.length
    );
  };

  return (
    <section className="py-16 bg-gray-200">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold mb-10">Image Gallery</h2>
        <div className="relative">
          <img
            src={images[currentIndex]}
            alt={`Gallery ${currentIndex + 1}`}
            className="w-full h-auto rounded-lg shadow-lg"
          />
          <button
            onClick={prevImage}
            className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-black/50 text-white h-full w-15 flex items-center justify-center shadow-md hover:bg-black/60 "
          >
            &lt;
          </button>
          <button
            onClick={nextImage}
            className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-black/50 text-white h-full w-15 flex items-center justify-center shadow-md hover:bg-black/60 "
          >
            &gt;
          </button>
        </div>
      </div>
    </section>
  );
};

export default ImageGallery;
