import React from "react";
import logo from '../assets/logo.png'

const Footer = () => {
  return (
    <footer className="bg-white  py-10">
      <div className="container mx-auto grid grid-cols-1 md:grid-cols-3 gap-8 px-5">
        {/* Logo Section */}
        <div className="flex my-auto border border-red-500">
          <img src={logo} alt="Logo" className="h-32 border border-red-500" />
          <h2 className="my-auto border border-red-500">
            ASMITA 2024
          </h2>
        </div>

        {/* Links Section */}
        <div className="flex flex-col items-center md:items-start">
          <h4 className="font-semibold text-lg mb-3">Quick Links</h4>
          <ul className="space-y-2">
            <li><a href="/about" className="text-gray-300 hover:underline">About Us</a></li>
            <li><a href="/services" className="text-gray-300 hover:underline">Services</a></li>
            <li><a href="/events" className="text-gray-300 hover:underline">Events</a></li>
            <li><a href="/contact" className="text-gray-300 hover:underline">Contact</a></li>
          </ul>
        </div>

        {/* Contacts Section */}
        <div className="flex flex-col items-center md:items-start">
          <h4 className="font-semibold text-lg mb-3">Contact Us</h4>
          <p className="text-gray-300">123 Main St, City, Country</p>
          <p className="text-gray-300">Phone: (123) 456-7890</p>
          <p className="text-gray-300">Email: info@example.com</p>
        </div>
      </div>

      {/* Copyright Statement */}
      <div className="mt-8 text-center text-sm text-gray-500 border-t border-gray-200 pt-4">
        Â© {new Date().getFullYear()} IIIT Allahabad. All rights reserved.
      </div>
    </footer>
  );
};

export default Footer;
