// authentication context
// src/context/AuthContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';
import axios from '../api';
import {jwtDecode} from 'jwt-decode';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
   const [user, setUser] = useState(null);

   useEffect(() => {
      const token = localStorage.getItem('token');
      if (token) {
         setUser(jwtDecode(token));
      }
      else{
        localStorage.removeItem('token');
        setUser(null);
      }
   }, []);

   const login = async (credentials) => {
    //   const res = await axios.post('/auth/login', credentials);
    //   const token = res.data.token;
    //   localStorage.setItem('token', token);
    //   setUser(jwtDecode(token));
   };

   const register = async (credentials) => {
    try {
        const res = await axios.post('/auth/register', credentials); // Send registration request
        const token = res.data.token; // Get the token from the response

        if (token) {
            localStorage.setItem('token', token); // Store the token in local storage
            const user = jwtDecode(token); // Decode the token to get user info
            setUser(user); // Set the user state with the decoded information
        } else {
            throw new Error('Token not received');
        }
    } catch (error) {
        console.error('Registration failed:', error.response?.data?.message || error.message);
        throw error; // Optionally re-throw the error for further handling
    }
 };

   const logout = () => {
      localStorage.removeItem('token');
      setUser(null);
   };

   return (
      <AuthContext.Provider value={{ user, login,register, logout }}>
         {children}
      </AuthContext.Provider>
   );
};

// Custom hook to use the AuthContext
export const useAuth = () => {
    return useContext(AuthContext);
 };
