// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import HomePage from './pages/HomePage';
import Dashboard from './pages/Dashboard'; // Make sure to create this component
import LoginPage from './pages/LoginPage';
import Register from './pages/RegisterPage';
import EditEvent from './pages/AdminPages/EditEvent';
import CreateEvent from './pages/CreateEvent';
import Sidebar from './components/Sidebar';
import './App.css'; // Make sure this line is at the top
import { CalendarPlus,HomeIcon,Boxes,CalendarCog,UserCircle,BarChart3,LayoutDashboard,Settings } from 'lucide-react';
import { SidebarItem } from './components/Sidebar';
import { AuthContext, AuthProvider } from './context/AuthContext';
import ConfirmEmail from './pages/ConfirmEmail';

function App() {
   return (
    <AuthProvider>
       <Router>
          <div className="app">
             <Sidebar> 
               <SidebarItem to="/" icon={<HomeIcon size={20}/>} text="Home" />
               <SidebarItem to="/Dashboard" icon={<LayoutDashboard size={20}/>} text="Dashboard" />
               <hr className='my-3'/>
               <SidebarItem to="/CreateEvent" icon={<CalendarPlus size={20}/>} text="Create Event" />
               <SidebarItem to="/EditEvent" icon={<CalendarCog size={20}/>} text="Edit Events" />
             </Sidebar>

             <div className="flex-1">
               <Routes>
                 <Route path="/" element={<HomePage />} />
                 <Route path="/Dashboard" element={<Dashboard />} />
                 <Route path="/login" element={<LoginPage />} />
                 <Route path="/register" element={<Register />} />
                 <Route path="/confirm-email/:token" element={<ConfirmEmail />} />
                  {/* admin only routes  */}
                 <Route path="/CreateEvent" element={<CreateEvent/>} />
                 <Route path="/EditEvent" element={<EditEvent/>} />
               </Routes>
             </div>

          </div>
       </Router>
    </AuthProvider>

   );
}

export default App;
