import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext'; // Make sure to import Auth context
import { useNavigate } from 'react-router-dom';

const Login = () => {
   const { login } = useAuth();
   const [email, setEmail] = useState('');
   const [password, setPassword] = useState('');
   const [error, setError] = useState(null); // State to hold error messages
   const navigate = useNavigate();

   const handleLogin = async (e) => {
      e.preventDefault();

      try {
         const response = await fetch('http://localhost:5000/api/login', { // Update with your backend URL
            method: 'POST',
            headers: {
               'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email, password }),
         });

         const data = await response.json();

         if (response.ok) {
            // On successful login, call the login method from context
            login(data.user); // Assuming data.user contains user details
            navigate('/dashboard'); // Redirect to dashboard
         } else {
            // Handle login error
            setError(data.error || 'Login failed, please try again.');
         }
      } catch (err) {
         setError('An error occurred. Please try again later.');
      }
   };

   function Button({ value }) {
      return (
         <button 
            onClick={handleLogin}
            className="mt-6 transition transition-all block py-3 px-4 w-full text-white font-bold rounded cursor-pointer bg-gradient-to-r from-indigo-600 to-purple-400 hover:from-indigo-700 hover:to-purple-500 focus:bg-indigo-900 transform hover:-translate-y-1 hover:shadow-lg">
            {value}
         </button>
      );
   }

   function Input({ type, id, name, label, placeholder, autofocus }) {
      return (
         <label className="text-gray-500 block mt-3">{label}
            <input
               autoFocus={autofocus}
               type={type} 
               id={id} 
               name={name} 
               placeholder={placeholder}
               onChange={(e) => (type === 'email' ? setEmail(e.target.value) : setPassword(e.target.value))}
               className="rounded px-4 py-3 w-full mt-1 bg-white text-gray-900 border border-gray-200 focus:border-indigo-400 focus:outline-none focus:ring focus:ring-indigo-100"/>
         </label>
      );
   }

   return (
      <div className="bg-gray-200 flex justify-center items-center h-screen">
         <div className="border-t-8 rounded-sm border-indigo-600 bg-white p-12 shadow-2xl w-96">
            <h1 className="font-bold text-center block text-2xl">Log In</h1>
            <form onSubmit={handleLogin}>
               <Input type="email" id="email" name="email" label="Email Address" placeholder="example@iiita.ac.in" autofocus={true} />
               <Input type="password" id="password" name="password" label="Password" placeholder="••••••••••" />
               {error && <p className="text-red-500">{error}</p>} {/* Display error message */}
               <Button value="Login" />
            </form>
         </div>
      </div>
   );
};

export default Login;
