import React, { useState, useEffect } from 'react';
import api from '../api'; // Adjust this to match your API setup

const LiveMatches = () => {
  const [liveMatches, setLiveMatches] = useState([]);

  useEffect(() => {
    const fetchLiveMatches = async () => {
      try {
        const response = await api.get('/matches');
        const currentTime = new Date();
        const live = response.data.filter(match => {
          const startTime = new Date(match.date + ' ' + match.time);
          const endTime = new Date(startTime.getTime() + match.duration * 60000); // Assuming match.duration is in minutes
          return currentTime >= startTime && currentTime <= endTime;
        });
        setLiveMatches(live);
      } catch (error) {
        console.error('Error fetching live matches:', error);
      }
    };

    fetchLiveMatches();
  }, []);

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 my-4">
      <h2 className="text-2xl font-bold mb-4">Live Matches</h2>
      {liveMatches.length > 0 ? (
        liveMatches.map((match, index) => (
          <div key={index} className="p-4 mb-4 border rounded-lg">
            <p><strong>Event:</strong> {match.event.name}</p>
            <p><strong>Teams/Players:</strong> {match.team1} vs {match.team2}</p>
            <p><strong>Venue:</strong> {match.venue}</p>
            <p><strong>Time:</strong> {match.time}</p>
          </div>
        ))
      ) : (
        <p>No live matches currently.</p>
      )}
    </div>
  );
};

export default LiveMatches;
