// src/pages/AdminPages/CreateAnnouncements.js
import React, { useEffect, useState } from 'react';
import api from '../../api'; // Adjust according to your axios setup

const CreateAnnouncements = () => {
  const [announcements, setAnnouncements] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  useEffect(() => {
    const fetchAnnouncements = async () => {
      try {
        const response = await api.get('/announcements');
        setAnnouncements(response.data);
      } catch (error) {
        console.error('Error fetching announcements:', error);
      }
    };

    fetchAnnouncements();
  }, []);

  const handleCreateAnnouncement = async (e) => {
    e.preventDefault();
    try {
      const token=localStorage.getItem('token');
      const response = await api.post('/announcements', { title, content },{
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setAnnouncements([...announcements, response.data]);
      setTitle('');
      setContent('');
    } catch (error) {
      console.error('Error creating announcement:', error);
    }
  };

  const handleDeleteAnnouncement = async (id) => {
    try {
        const token=localStorage.getItem('token');
      await api.delete(`/announcements/${id}`,{
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setAnnouncements(announcements.filter(announcement => announcement._id !== id));
    } catch (error) {
      console.error('Error deleting announcement:', error);
    }
  };

  return (
  <div className="max-w-4xl mx-auto p-6 bg-gray-100 rounded-lg shadow-md">
    <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Organizer Announcements</h2>

    <form onSubmit={handleCreateAnnouncement} className="mb-6">
      <div className="flex flex-col md:flex-row mb-4">
        <input
          type="text"
          placeholder="Announcement Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border border-gray-300 rounded-lg p-3 mr-0 md:mr-4 mb-4 md:mb-0 focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          required
        />
        <textarea
          placeholder="Announcement Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          className="border border-gray-300 rounded-lg p-3 mr-0 md:mr-4 focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
          required
        />
        <button type="submit" className="bg-blue-500 text-white rounded-lg p-3 mt-2 md:mt-0 hover:bg-blue-600 transition">
          Create Announcement
        </button>
      </div>
    </form>

    {announcements.length > 0 ? (
      announcements.map((announcement) => (
        <div key={announcement._id} className="bg-white border border-gray-200 rounded-lg shadow-sm p-4 mb-4 transition hover:shadow-lg">
          <h3 className="text-xl font-bold text-gray-800">{announcement.title}</h3>
          <p className="text-gray-700">{announcement.content}</p>
          <button
            onClick={() => handleDeleteAnnouncement(announcement._id)}
            className="bg-red-500 text-white rounded-lg p-2 mt-2 hover:bg-red-600 transition"
          >
            Delete
          </button>
        </div>
      ))
    ) : (
      <p className="text-gray-600">No announcements found.</p>
    )}
  </div>
);

};

export default CreateAnnouncements;
