// src/pages/AdminPages/CreateAnnouncements.js
import React, { useEffect, useState } from 'react';
import api from '../../api'; // Adjust according to your axios setup

const CreateAnnouncements = () => {
  const [announcements, setAnnouncements] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  useEffect(() => {
    const fetchAnnouncements = async () => {
      try {
        const response = await api.get('/announcements');
        setAnnouncements(response.data);
      } catch (error) {
        console.error('Error fetching announcements:', error);
      }
    };

    fetchAnnouncements();
  }, []);

  const handleCreateAnnouncement = async (e) => {
    e.preventDefault();
    try {
      const token=localStorage.getItem('token');
      const response = await api.post('/announcements', { title, content },);
      setAnnouncements([...announcements, response.data]);
      setTitle('');
      setContent('');
    } catch (error) {
      console.error('Error creating announcement:', error);
    }
  };

  const handleDeleteAnnouncement = async (id) => {
    try {
      await api.delete(`/announcements/${id}`);
      setAnnouncements(announcements.filter(announcement => announcement._id !== id));
    } catch (error) {
      console.error('Error deleting announcement:', error);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Organizer Announcements</h2>

      <form onSubmit={handleCreateAnnouncement} className="mb-4">
        <input
          type="text"
          placeholder="Announcement Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 mr-2"
          required
        />
        <textarea
          placeholder="Announcement Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          className="border p-2 mr-2"
          required
        />
        <button type="submit" className="bg-blue-500 text-white p-2">Create Announcement</button>
      </form>

      {announcements.length > 0 ? (
        announcements.map((announcement) => (
          <div key={announcement._id} className="border p-4 mb-4 rounded">
            <h3 className="text-xl font-bold">{announcement.title}</h3>
            <p>{announcement.content}</p>
            <button
              onClick={() => handleDeleteAnnouncement(announcement._id)}
              className="bg-red-500 text-white p-2 mt-2"
            >
              Delete
            </button>
          </div>
        ))
      ) : (
        <p>No announcements found.</p>
      )}
    </div>
  );
};

export default CreateAnnouncements;
